function updateCartCount(){for(var t=0,e=0,a=0;a<cartQty.length;a++)t+=parseInt(cartQty[a].value),e+=parseFloat(cartItemPrice[a].innerHTML.slice(1));document.getElementsByClassName("cart-count")[0].innerHTML=t,cartTotal[0].innerHTML="$"+e.toFixed(2)}function addItem(){for(var t=0;t<addToCart.length;t++)addToCart[t].addEventListener("click",function(){var t=this.parentElement.firstElementChild.firstElementChild.textContent,e=items[t],a=this.previousElementSibling.firstElementChild.value,n=(e*a).toFixed(2),r=document.createElement("p");r.innerHTML='<span class="item">'+t+'</span> <input class="qty" value='+a+'> <span class="cart-price">$'+n+'</span><button class="remove">Remove</button>',cartCont[0].appendChild(r),removeItem(),updateCartCount(),updateItemQty()})}function removeItem(){for(var t=0;t<removeFromCart.length;t++)removeFromCart[t].addEventListener("click",function(){var t=this.parentElement,e=document.getElementsByClassName("tooltip-text");t.remove(),updateCartCount()})}function updateItemQty(){for(var t=0;t<cartQty.length;t++)cartQty[t].addEventListener("change",function(){var t=this.parentElement,e=t.firstElementChild.innerHTML,a=t.getElementsByClassName("cart-price"),n=items[e],r=this.value,o=parseFloat(n*r);console.log(o),a[0].innerHTML="$"+o.toFixed(2),updateCartCount()})}var items={Train:"18.95",BulletTime:"15.95",Klutz:"13.95",Global:"13.95",Blunt:"15.95",Crowd:"17.95",Banksy:"18.95",Pico:"17.95",Hex:"17.95",Byte:"18.95"},addToCart=document.getElementsByClassName("add"),removeFromCart=document.getElementsByClassName("remove"),cartCont=document.getElementsByClassName("tooltip-text"),cartQty=document.getElementsByClassName("qty"),cartItemPrice=document.getElementsByClassName("cart-price"),cartTotal=document.getElementsByClassName("amount");addItem(),removeItem();