function updateCartCount(){for(var t=0,e=0;e<cartQty.length;e++)t+=parseInt(cartQty[e].innerHTML);document.getElementsByClassName("cart-count")[0].innerHTML=t}function addItem(){for(var t=0;t<addToCart.length;t++)addToCart[t].addEventListener("click",function(){var t=this.parentElement.firstElementChild.firstElementChild.textContent,e=this.parentElement.firstElementChild.firstElementChild.nextSibling.textContent;e=e.slice(1);var n=this.previousElementSibling.firstElementChild.value,a=(e*n).toFixed(2);console.log(t+": "+e+" for "+n);var r=document.createElement("p");r.innerHTML='<span class="item">'+t+'</span> <span class="qty">'+n+'</span> <span class="cart-price">$'+a+'</span><button class="remove">Remove</button>',cartCont[0].appendChild(r),removeItem(),updateCartCount()})}function removeItem(){for(var t=0;t<removeFromCart.length;t++)removeFromCart[t].addEventListener("click",function(){var t=this.parentElement,e=document.getElementsByClassName("tooltip-text");t.remove(),updateCartCount()})}var addToCart=document.getElementsByClassName("add"),removeFromCart=document.getElementsByClassName("remove"),cartCont=document.getElementsByClassName("tooltip-text"),cartQty=document.getElementsByClassName("qty");addItem(),removeItem();